let lastScroll=0;const header=document.querySelector(".header"),burger=document.querySelector(".hamburger"),overlay=document.querySelector(".menu-overlay"),closeBtn=document.querySelector(".close-menu");window.addEventListener("scroll",()=>{const e=window.pageYOffset;e>lastScroll&&e>100?header.classList.add("hide"):header.classList.remove("hide"),lastScroll=e}),burger.addEventListener("click",()=>{overlay.classList.add("active"),document.body.style.overflow="hidden"}),closeBtn.addEventListener("click",()=>{overlay.classList.remove("active"),document.body.style.overflow=""})